import React, { useState } from "react";
import InputForm from "./InputForm";
import TotalCostTripForm from "./TotalCostTripForm";
export default function App() {
  const [vehicles, setVehicle] = useState([]);
  const [totalCostTrip, setTotalCostTrip] = useState(
    {
      baseRate: 20,
      breakDown: 0,
      SubTotal: 0,
      discount: 0,
      vehicleNumber: '',
      total: 0
    }
  );
  console.log(vehicles)
  const formula = (vehicle) => {
    let tex = 20 + (vehicle.interchangeName.value * 0.2)
    console.log(tex)
    setTotalCostTrip({
      baseRate: 20,
      breakDown: 0,
      SubTotal: 0,
      discount: 0,
      vehicleNumber: vehicle.numberPlate.value,
      total: 0
    })
  }
  return (
    <div class="container">
      <div class="row top30">
        <div class="col-5 border-add">
          <InputForm
            getVehicle={(vehicle) => {
              setVehicle([...vehicles, vehicle])
            }} />

        </div>
        <div class="col-1" />
        <div class="col-5 border-add">
          <InputForm
            exit
            vehicles={vehicles}
            getVehicle={(vehicle) => {
              console.log("vehicle", vehicle)
              formula(vehicle)
            }} />

        </div>
      </div>
      <div class="row top30">
        <div class="col">
          <TotalCostTripForm totalCostTrip={totalCostTrip} />
        </div>
      </div>
      <div class="row top30">
        <span>Total Vsehicles</span>

        {vehicles.map((item) => {
          return (
            <div class="col-2">
              <button
                className='button'
                type="submit"
              >
                <span>{item.numberPlate}</span>
              </button>
            </div>
          )
        })}
      </div>
    </div>
  );
}
